from flask import Flask, request, render_template
app = Flask(__name__)

@app.route('/')
def home():
    return render_template('home.html')

@app.route('/checkbox')
def checkbox():
    return render_template('checkbox.html')  

@app.route('/risultato',methods=['post'])
def risultato():
    peso = int(request.form['peso'])
    altezza = float(request.form['altezza'])
    calcolo = peso / (altezza * altezza)

    if calcolo < 18.5:
        esito="Sei Sottopeso Amico","ti consiglio di mangiare cose del genere"
        img = 'data:image/jpeg;base64,'

    elif calcolo <= 24.9:
        esito="Sei Normopeso Amico","ti consiglio di mangiare cose del genere"
        img ='data:image/jpeg;base64,'
    elif calcolo <= 29.9:
        esito="Sei Sovrappeso  Amico","ti consiglio di mangiare cose del genere"
        img ='data:image/jpeg;base64,'

    else:
        esito="Sei Sovrappeso  Amico.ti consiglio di mangiare cose del genere"
        img ='data:image/jpeg;base64,'

    return render_template('riepilogo.html',esito = esito,img = img )
    
if __name__ == '__main__':
  app.run(host='0.0.0.0', port=3245, debug=True)